import readline from "readline";

export function askProjectProblem() {
  return new Promise((resolve) => {
    const rl = readline.createInterface({
      input: process.stdin,
      output: process.stdout,
    });

    rl.question(
      "ðŸ§  In one line, what problem does this project solve?\n> ",
      (answer) => {
        rl.close();
        resolve(answer.trim());
      }
    );
  });
}
